// FamilyMatrix.jsx
import React from "react";
import { FaUserTie, FaChalkboardTeacher, FaGraduationCap, FaSchool, FaUserAltSlash, FaUtensils } from "react-icons/fa";
import { HiOutlineCheck } from "react-icons/hi";
import CircleIcon from "./CircleIcon";

/*
  Family rows on left, products columns on right.
  You can adjust the `matrix` boolean values to control checkmarks.
*/

const family = [
  { id: "p", age: "45Y", label: "Businessman", icon: <FaUserTie size={28} /> },
  { id: "t", age: "42Y", label: "School Teacher", icon: <FaChalkboardTeacher size={28} /> },
  { id: "c", age: "18Y", label: "College", icon: <FaGraduationCap size={28} /> },
  { id: "s", age: "8Y", label: "School", icon: <FaSchool size={28} /> },
  { id: "r", age: "70Y", label: "Retired", icon: <FaUserAltSlash size={28} /> },
  { id: "h", age: "65Y", label: "Housewife", icon: <FaUtensils size={28} /> },
];

const columns = ["Mutual Fund", "Fixed Income", "Insurance", "Others"];

// example grid that maps family index -> which columns have checks
const matrix = [
  // Businessman
  [true, true, true, true],
  // Teacher
  [true, true, true, true],
  // College
  [true, true, true, false],
  // School
  [false, true, false, false],
  // Retired
  [false, true, true, false],
  // Housewife
  [true, false, true, true],
];

const CheckBadge = () => (
  <div className="inline-flex items-center justify-center w-10 h-10 rounded-full bg-green-600 shadow">
    <HiOutlineCheck size={18} className="text-white" />
  </div>
);

const FamilyMatrix = () => {
  return (
    <section className="px-6 lg:px-12 py-12">
      <h2 className="text-3xl lg:text-4xl font-extrabold mb-8">
        We Don’t Target Individuals, We Target Families
      </h2>

      <div className="grid grid-cols-1 lg:grid-cols-5 gap-6 items-start">
        {/* Left column - family icons stacked (takes 1 col on lg) */}
        <div className="space-y-8 lg:col-span-1">
          {family.map((f, i) => (
            <div key={f.id} className="flex items-center gap-6">
              <div>
                <CircleIcon size={84} bg="#ff6161">
                  {/* Use the passed icon (white stroke icons look better if they are line/svgs) */}
                  <div style={{ color: "white" }}>{f.icon}</div>
                </CircleIcon>
              </div>
              <div>
                <div className="text-lg font-semibold">{f.age}</div>
                <div className="text-sm text-gray-800">{f.label}</div>
              </div>
            </div>
          ))}
        </div>

        {/* Right area - matrix: columns header + rows with checks */}
        <div className="lg:col-span-4">
          <div className="overflow-x-auto">
            <table className="w-full table-fixed border-collapse">
              <thead>
                <tr>
                  {columns.map((c, idx) => (
                    <th
                      key={c}
                      className={`text-left py-6 px-6 text-lg font-semibold border-t border-b border-gray-200`}
                    >
                      {c}
                    </th>
                  ))}
                </tr>
              </thead>

              <tbody>
                {family.map((f, rowIdx) => (
                  <tr key={f.id} className="h-28">
                    {columns.map((col, colIdx) => (
                      <td
                        key={col}
                        className="px-6 text-center align-middle border-b border-gray-200"
                      >
                        {matrix[rowIdx] && matrix[rowIdx][colIdx] ? (
                          <div className="flex items-center justify-center">
                            <CheckBadge />
                          </div>
                        ) : (
                          <div className="h-10" /> /* placeholder to keep row height consistent */
                        )}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Optional small caption under 'Others' header */}
          <div className="text-xs text-gray-500 mt-2">
            (NPS, PMS, AIF, Loans, Gold, NCDs/Bonds, Peer to Peer Lending - P2P)
          </div>
        </div>
      </div>
    </section>
  );
};

export default FamilyMatrix;
