// HeroCurve.jsx
import React from "react";

/*
  Place your image at: public/hero-curve.png
  Or update imageSrc to the correct path, for example:
  import heroImg from "../assets/Screenshot 2025-09-17 at 2.09.24 PM.png"
  and then use src={heroImg}
*/

const imageSrc = "/hero-curve.png"; // <- update if needed

const Marker = ({ top, left, labelTitle, labelSubtitle, labelRight }) => {
  return (
    <>
      {/* marker bubble */}
      <div
        className="absolute w-12 h-12 rounded-lg bg-white shadow-lg flex items-center justify-center"
        style={{
          top,
          left,
          transform: "translate(-50%, -50%)",
        }}
      >
        <div className="w-4 h-4 rounded-sm" style={{ background: "#E53E3E" }} />
      </div>

      {/* label box (title + subtitle) */}
      <div
        className="absolute font-bold text-black max-w-xs"
        style={{
          top: `calc(${top} + 2.6rem)`,
          left,
          transform: "translateX(-50%)",
          textAlign: "center",
        }}
      >
        <div className="text-xl">{labelTitle}</div>
        {labelSubtitle && <div className="text-sm font-normal mt-1">{labelSubtitle}</div>}
      </div>

      {/* optional right big label (for top-right marker in screenshot) */}
      {labelRight && (
        <div
          className="absolute font-extrabold text-3xl"
          style={{
            top: `calc(${top} - 1.2rem)`,
            left: `calc(${left} + 14rem)`,
            transform: "translateY(-50%)",
          }}
        >
          {labelRight}
        </div>
      )}
    </>
  );
};

const HeroCurve = () => {
  return (
    <section className="w-full bg-white">
      <div className="container mx-auto px-6 lg:px-12 py-12">
        <div className="relative bg-white rounded-none overflow-visible" style={{ minHeight: 520 }}>
          {/* Left heading */}
          <div className="absolute top-6 left-6 lg:left-12 lg:top-12 z-20">
            <h1 className="text-3xl lg:text-5xl font-extrabold tracking-tight">Grow your Financial Business with Nivesh</h1>
            <p className="mt-3 text-lg text-gray-700 font-medium">From Scratch to Sky</p>
          </div>

          {/* Right/center big image (curve) */}
          <div className="absolute inset-0 flex items-center justify-end">
            <img
              src={imageSrc}
              alt="curve"
              className="w-full object-contain max-w-[1400px] pointer-events-none select-none"
              style={{ height: 520, objectFit: "contain" }}
            />
          </div>

          {/* Markers + labels - positions are percentages; tweak to perfect */}
          {/* Left-bottom marker */}
          <Marker
            top="78%"   /* adjust vertical position */
            left="12%"  /* adjust horizontal position */
            labelTitle="No. of Customers"
          />

          {/* Mid marker */}
          <Marker
            top="58%"
            left="42%"
            labelTitle="Investment"
            labelSubtitle="Per Customer"
          />

          {/* Top-right marker with large '= Higher AUM' label */}
          <Marker
            top="30%"
            left="76%"
            labelTitle="Customer"
            labelSubtitle="Retention %"
            labelRight="= Higher AUM"
          />

          {/* Big X's on curve (decorative) */}
          <div
            className="absolute text-5xl font-extrabold"
            style={{ top: "66%", left: "28%", transform: "translate(-50%, -50%)" }}
          >
            X
          </div>
          <div
            className="absolute text-5xl font-extrabold"
            style={{ top: "48%", left: "56%", transform: "translate(-50%, -50%)" }}
          >
            X
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroCurve;
